package main

import "fmt"

func main() {
	fmt.Println(maxNumberOfBalloons(""))
}

func maxNumberOfBalloons(text string) int {
	textMapper := make(map[rune]int)
	ball := "balloon"
	if len(text) < len(ball) {
		return 0
	}
	for _, letter := range text {
		if _, ok := textMapper[letter]; ok {
			textMapper[letter]++
		} else {
			textMapper[letter] = 1
		}
	}

	if _, ok := textMapper['l']; ok {
		textMapper['l'] = textMapper['l'] / 2
	}
	if _, ok := textMapper['o']; ok {
		textMapper['o'] = textMapper['o'] / 2
	}

	counter := textMapper['b']
	for _, letter := range ball {
		if val, ok := textMapper[letter]; ok {
			if val < counter {
				counter = val
			}
		} else {
			counter = 0
			break
		}
	}
	return counter
}
